import axios from 'axios';


const API_URL = 'http://localhost:9080'; 

export const getAllGreves = () => axios.get(`${API_URL}/buscar/greves`);
export const getGreveById = (id) => axios.get(`${API_URL}/buscar/greve/${id}`);
export const createGreve = async (formData) => {
    return axios.post('/cadastrar/greve', formData);
  };
  
  export const updateGreve = async (id, formData) => {
    return axios.put(`/atualizar/greve/${id}`, formData);
  };
export const deleteGreve = (id) => axios.delete(`${API_URL}/deletar/greve/${id}`);
export const updateGreveFinalData = (id, data) => axios.patch(`${API_URL}/atualizar/fim/greve/${id}`, data);
